mapper.select:
select USER_CODE
from ${TableName}
where BUSI_DATE = '${RPT_DUR}'
and DEPT_CODE = '${RPT_DEPT}'

    and ${SallaryType}

		select * 
		from ${TableName}


            (BUSI_DATE = #{item.BUSI_DATE} and USER_CODE = #{item.USER_CODE} and USER_CODE != #{item.USER_CODE__})

		select 
		    * ,

		from ${pd.TableName}
		where BUSI_DATE = '${pd.SystemDateTime}'
		and DEPT_CODE = '${pd.DepartCode}'

			${pd.filterWhereResult}


		    and USER_CODE = '${pd.UserCode}'


		    and ${pd.SallaryType}


			order by ${sidx} ${sord} 


              limit #{currentResult}, #{rowNum}

select count(*) 
from ${pd.TableName}
where BUSI_DATE = '${pd.SystemDateTime}'
and DEPT_CODE = '${pd.DepartCode}'

	${pd.filterWhereResult}


    and USER_CODE = '${pd.UserCode}'


    and ${pd.SallaryType}

select ${pd.Userdata} 
from ${pd.TableName}
where BUSI_DATE = '${pd.SystemDateTime}'
and DEPT_CODE = '${pd.DepartCode}'

	${pd.filterWhereResult}


    and USER_CODE = '${pd.UserCode}'


    and ${pd.SallaryType}

mapper.delete:
delete from ${TableName}
where BUSI_DATE = #{BUSI_DATE__} 
and USER_CODE = #{USER_CODE__}

		delete from ${TableName}
		where

            BUSI_DATE = #{item.BUSI_DATE__} and USER_CODE = #{item.USER_CODE__}

	delete from ${TableName}
	where BUSI_DATE = #{BUSI_DATE}
    and DEPT_CODE = #{DEPT_CODE} 

	    and USER_GROP = #{SallaryType}

mapper.select:
select * 
from ${pd.TableName}
where BUSI_DATE = '${pd.SystemDateTime}'
and DEPT_CODE = '${pd.DepartCode}'

    and ${pd.SallaryType}


	order by ${sidx} ${sord}

select * 
from ${pd.TableName}
where DEPT_CODE = '${pd.DepartCode}'

    and ${pd.SallaryType}

order by BUSI_DATE desc
limit 1
