mapper.select:
		select 


		from 

		where BUSI_DATE = '${pd.SystemDateTime}'
		and DEPT_CODE = '${pd.DepartCode}'

			${pd.filterWhereResult}


		    and USER_CODE = '${pd.UserCode}'


			order by ${sidx} ${sord} 


              limit #{currentResult}, #{rowNum}

select count(*) from where BUSI_DATE = '${pd.SystemDateTime}' and DEPT_CODE = '${pd.DepartCode}'
${pd.filterWhereResult}
and USER_CODE = '${pd.UserCode}'

select ${pd.Userdata} 
from  
where BUSI_DATE = '${pd.SystemDateTime}'
and DEPT_CODE = '${pd.DepartCode}'

	${pd.filterWhereResult}


    and USER_CODE = '${pd.UserCode}'

mapper.delete:
		delete from

		where

            BUSI_DATE = #{item.BUSI_DATE__} and USER_CODE = #{item.USER_CODE__}

	delete from

	where BUSI_DATE = #{BUSI_DATE}
    and DEPT_CODE = #{DEPT_CODE}

mapper.select:
select ${GroupbyFeild}, sum(SOC_INC_BASE) SOC_INC_BASE, sum(PER_ENDW_INS) PER_ENDW_INS, sum(PER_MED_INS) PER_MED_INS, sum(PER_UNEMPL_INS) PER_UNEMPL_INS, sum(PER_TOTAL) PER_TOTAL, sum(DEPT_ENDW_INS) DEPT_ENDW_INS, sum(DEPT_MED_INS) DEPT_MED_INS, sum(DEPT_UNEMPL_INS) DEPT_UNEMPL_INS, sum(EMPT_INJ_INS) EMPT_INJ_INS, sum(MATY_INS) MATY_INS, sum(DEPT_TOTAL) DEPT_TOTAL,
sum(CUST_COL1) CUST_COL1, sum(CUST_COL2) CUST_COL2, sum(CUST_COL3) CUST_COL3, sum(CUST_COL4) CUST_COL4, sum(CUST_COL5) CUST_COL5, sum(CUST_COL6) CUST_COL6 from
where BUSI_DATE = '${BUSI_DATE}' and DEPT_CODE = '${DEPT_CODE}' group by ${GroupbyFeild}
