mapper.update:
update
set
COL_NAME = #{COL_NAME}, DISP_ORDER = #{DISP_ORDER}, DICT_TRANS = #{DICT_TRANS}, COL_HIDE = #{COL_HIDE}, COL_SUM = #{COL_SUM}, COL_AVE = #{COL_AVE} where DEPT_CODE = #{DEPT_CODE} and TABLE_CODE = #{TABLE_CODE} and COL_CODE = #{COL_CODE}

mapper.select:
select

from



		and (
		DEPT_CODE = #{pd.DEPT_BIANMA}
		)


		and (
		TABLE_CODE = #{pd.TABLE_CODE}
		)

	select

	from 

	where DEPT_CODE = #{DEPT_CODE}
    and TABLE_CODE = #{TABLE_CODE}
    order by DISP_ORDER
